mapper.select:
SELECT n_id, n_title, n_createdAt, n_updatedAt
FROM (
    SELECT n_id, n_title, n_createdAt, n_updatedAt, ROWNUM AS num
    FROM (
        SELECT n_id, n_title, n_createdAt, n_updatedAt
        FROM notice_board
        ORDER BY TO_NUMBER(n_id) DESC
    )
)
WHERE num BETWEEN #{start} AND #{end}
